import React, { Component } from "react";
//import "../App.css";
import Joyride from "react-joyride";


class JoyrideExport extends Component {
  state = {
    steps: [
      {
        target: ".my-first-step",
        content: "This is super awesome feature ",
      },
      {
        target: ".my-other-step",
        content: "Everyone's learning Joyride!",
      },
    ],
  };
  render() {
    const { steps } = this.state;
    return (
      <div className="App">


        <Joyride steps={steps} />

        <header className="App-header ">

          <a className="App-link my-other-step" href="https://github.com/gilbarbara/react-joyride"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn Joyride
          </a>
        </header>



      </div>

    );
  }
}

export default JoyrideExport;
