import React, { useState, useEffect } from 'react';
import { Rating } from 'primereact/rating';
import { Button } from 'primereact/button';
import './DataView.css';
import { getPeople } from "../api/index";
import { Paginator } from 'primereact/paginator';
import img0 from '../images/people/0.jpeg';
import img1 from '../images/people/1.jpeg';
import img2 from '../images/people/2.jpeg';
import img3 from '../images/people/3.jpeg';
import img4 from '../images/people/4.jpeg';
import img5 from '../images/people/5.jpeg';
import img6 from '../images/people/6.jpeg';
import img7 from '../images/people/7.jpeg';
import "./spinner.css";

const generateRandImg = () => {

    if (Math.floor(Math.random() * 7) === 0 ) return img0;
    if (Math.floor(Math.random() * 7) === 1 ) return img1;
    if (Math.floor(Math.random() * 7) === 2 ) return img2;
    if (Math.floor(Math.random() * 7) === 3 ) return img3;
    if (Math.floor(Math.random() * 7) === 4 ) return img4;
    if (Math.floor(Math.random() * 7) === 5 ) return img5;
    if (Math.floor(Math.random() * 7) === 6 ) return img6;
    if (Math.floor(Math.random() * 7) === 7 ) return img7;
    return img7;

}

const DataViewLazyDemo = () => {
    const [isLoading, setIsLoading] = useState(true);
    const [data, setData] = useState([]);
    const [first, setFirst] = useState(0);
    const [rows, setRows] = useState(1);
    const onPageChange = (e: any) => {
        setFirst(e.first+1);
        setRows(e.rows);
    }

    useEffect(() => {
        getPeople({ page: first+1 })
      .then((res) => {
         setData(res.data.results)
        });
    }, [first]);

    useEffect(() => {
        getPeople({ page: 1 })
      .then((res) => {
         setData(res.data.results)
        }).then(() => {
            console.log(`loadet`)
            setIsLoading(true)
        });
    }, []);

    const renderGridItem = (data: any) => {


        return (
            <div className="spinner-container">
      <div className="loading-spinner"></div>
            <div className='locationGossip'>{

            data.map((data: any, key: number)=>{ 
                return (

        <div className="col-3 "  key={key}>
                <div className="product-grid-item card">
                    <div className="product-grid-item-top">
                        <div>
                            <i className="pi pi-tag product-category-icon"></i>
                            <span className="product-category">{data.height}</span>
                        </div>
                        <span className={`product-badge status-${data.inventoryStatus}`}>{data.mass}</span>
                    </div>

                    <div className="product-grid-item-content">
                    <img src={generateRandImg()} onError={(e) => console.log(`error`)}alt={data.name} />
                        <div className="product-name">{data.name}</div>
                        <div className="product-description">{data.description}</div>
                        <Rating value={data.mass / 20} readOnly cancel={false}></Rating>
                    </div>
                    <div className="product-grid-item-bottom">
                        <span className="product-price">{data.price}</span>
                        <Button icon="pi pi-shopping-cart" label="By" disabled={data.inventoryStatus === 'OUTOFSTOCK'}></Button>
                    </div>
                </div>
            </div>

            )

        })
    }
    <Paginator first={first} rows={rows} totalRecords={120}  onPageChange={onPageChange}></Paginator>
     </div></div>
        ); 
    }

    return renderGridItem(data)
}

export default DataViewLazyDemo
